package com.example.josep.reminderbeta.Models;import com.google.firebase.database.IgnoreExtraProperties;// [START blog_user_class]@IgnoreExtraPropertiespublic class User {	public String email;	public String name;	public String username;	public User() {		// Default constructor required for calls to DataSnapshot.getValue(User.class)	}	public User(String email, String name, String username) {		this.email = email;		this.name = name;		this.username = username;	}	public String getUsername() {		return username;	}}// [END blog_user_class]